object MoneyBoxDataFactory {


    fun <T> generateMockDataType(classz: Class<out T>, jsonString: String): T {
        return Gson().fromJson(jsonString, classz)
    }


    fun <T> makeMultipleMockDataTypes(
        classz: Class<out T>,
        jsonString: String,
        count: Int
    ): MutableList<T> {
        val listOfentries = mutableListOf<T>()
        repeat(count) {
            listOfentries.add(generateMockDataType(classz, jsonString))
        }
        return listOfentries
    }


